hudson.tasks.Shell.command:
#!/bin/bash ### CREATE OUTPUT DIRECTORY
. $HOME/src/jenkins/config.git/scripts/common_jenkins_functions.sh job_step_header "CREATING OUTPUT DIRECTORY: ${WORKSPACE}/output"
mkdir ${WORKSPACE}/output

#!/bin/bash ### BUILD LOOP-AES
LOOP_AES_DIR=$(ls -1 ${WORKSPACE} \ | grep 'loop-AES' | egrep -v 'latest|log$') LINUX_KERNEL_VERSION=$(cat ${LOOP_AES_DIR}/linux-kernel.version) LOOP_AES_VERSION=$(cat ${LOOP_AES_DIR} | sed 's/^loop-AES-//')
. $HOME/src/jenkins/config.git/scripts/common_jenkins_functions.sh job_step_header "BUILDING LOOP-AES KERNEL MODULE - ${LOOP_AES_VERSION}"
cd ${WORKSPACE}/${LOOP_AES_DIR}
time make -j4 CC=${BUILD_CC} \ INSTALL_MOD_PATH=${WORKSPACE}/output \ LINUX_SOURCE=${WORKSPACE}/linux-${LINUX_KERNEL_VERSION} \ KEYSCRUB=y PADLOCK=y INTELAES=y EXTRA_CIPHERS=y
