hudson.tasks.Shell.command:
echo "==== Resetting PATH environment variable ====" export PATH=".:/mingw/bin:/bin:/usr/bin:/c/WINDOWS/system32"
BUILD_DIR="${PKG_NAME}-${PKG_VERSION}/win32"
echo "==== Changing to build directory: ${BUILD_DIR} ====" cd ${BUILD_DIR}
echo "PATH is: $PATH" echo "PWD is: $PWD"
#echo "-- Sleeping 5 seconds --" #sleep 5
#echo "==== Resetting file permissions in ../utils ====" #/bin/find.exe ../utils -type f -exec /bin/chmod.exe 644 '{}' \;
#echo "==== Permissions of files in ../utils ====" #/bin/ls.exe -l ../utils
echo "==== Running 'dmake' in ${PKG_NAME}-${PKG_VERSION}/win32 ====" #time dmake -v time dmake
echo "==== Running 'dmake test' in ${PKG_NAME}-${PKG_VERSION}/win32 ====" #time dmake -v test $(time dmake test)
echo "==== Running 'dmake install' in ${PKG_NAME}-${PKG_VERSION}/win32 ====" time dmake -v install
